import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios'
import { AuthContext } from '../../context/AuthContext';
import { Storage } from '@capacitor/storage';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';
// import imagess from '../../components/images/Annotation 2024-01-23 201729.png';
import "./postImg.css"

function PostImg() {

    const [img, setImgFile] = useState('');
    const [title, setTitle] = useState('');
    const [discraption, setDiscraption] = useState('');
    const [images, setImages] = useState([]);
    const [name, setName] = useState('');
    const [avatar, setAvatr] = useState('');
    // const [dalog, setDalog] = useState(false);
    // const [loading, setLoading] = useState(false);
    const [postId, setPostId] = useState('');

    const history = useHistory();

    const { jwt } = useContext(AuthContext);


    useEffect(() => {
        getData()
    }, []);


    // const PostLike = async () => {
    //     const data = {
    //         Like,
    //     }
    //     try {
    //         const res = await axios.put(`http://localhost:4000/post/${postId}/like`, data, {
    //             headers: {
    //                 Authorization: jwt
    //             }
    //         }).then(res => console.error(res))
    //         setPostId(res.data.data)
    //     } catch (e) {
    //         console.log(e)
    //     }
    // }


    const getData = async () => {
        try {
            // setLoading(true)
            const posts = await axios.get(`http://localhost:4000/post/65bb6b20c99df721fa772db0/get-post`)
            // const getPost = posts.data.data.map(item => item.img)
            setName(posts.data.data.author.name)
            // setImages(getPost)
            setAvatr(posts.data.data.author.avatar)
            setDiscraption(posts.data.data.discraption)
            setTitle(posts.data.data.title)
            console.log(posts.data.data.author.avatar)

            // setLoading(false)
        } catch (e) {
            console.log(e)
            // setLoading(false)
        }
    }
    return (
        <>
            <div>
                <div>
                    <div>
                        <img src={avatar} alt='null'/>
                        <p>{name}</p>

                        {
                            images.map((img,index)=> {
                                <img key={index} src={img} alt='Error'/>
                            })
                        }

                        <p>{title}</p>
                        <p>{discraption}</p>
                    </div>
                </div>
            </div>
        </>
    )
}

export default PostImg
