    const data = { 
      title,
      discraption
    }
    const newPost = await axios.post('http://localhost:4000/post', data).then(res=> res)
  }
    
  return (
    <div className='all'>
        <div className='add-container'>
            <div className='form-container'>
                    <label>Add Image</label>
                    <input type='file'/>
                    <label>Title</label>
                    <input className='text-input' type='text'/>
                    <label>Dicraptions</label>
                    <textarea value={discraption} type='text'/>
                    <button className='post-button' type='submit'>نشر</button>
            </div>
        </div>
    </div>
  )
}

export default AddNewPost
