import React, { useContext, useEffect, useState } from 'react';
import './AddNewPost.css';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext';
import blob from 'blob';
function AddNewPost() {

  const { jwt } = useContext(AuthContext);
  const [img, setImgFile] = useState(null);
  const [title, setTitle] = useState('');
  const [discraption, setDiscraption] = useState('');

  const onSubmit = async () => {
    const formData = new FormData();
    formData.append('img', img);
    formData.append('title', title);
    formData.append('discraption', discraption);

    try {
      const newPost = await axios.post('http://localhost:4000/post', formData, {
        headers: { 
          Authorization: jwt,
        },
      });
      console.log(newPost.data); // افحص البيانات المرتجعة
    } catch (e) {
      console.log(e);
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setImgFile(file);
  };

  return (
    <div className='all'>
      <div className='add-container'>
        <div className='form-container'>
          <label>Add Image</label>
          {img ? (
            <img
              style={{
                width: '200px',
                height: '200px',
                maxWidth: '200px',
                maxHeight: '200px',
                objectFit: 'cover',
              }}
              alt='Error'
              src={URL.createObjectURL(img)}
            />
          ) : (
            <input onChange={handleFileChange} type='file' />
          )}

          <label>Title</label>
          <input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className='text-input'
            type='text'
          />
          <label>Dicraptions</label>
          <textarea
            value={discraption}
            onChange={(e) => setDiscraption(e.target.value)}
            type='text'
          />
          <button className='post-button' onClick={onSubmit} type='submit'>
            نشر
          </button>
        </div>
      </div>
    </div>
  );
}

export default AddNewPost
