import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios'
import { AuthContext } from '../../context/AuthContext';
import { Storage } from '@capacitor/storage';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';
// import imagess from '../../components/images/Annotation 2024-01-23 201729.png';
import "./postImg.css"
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';


function PostImg() {

    const [title, setTitle] = useState('');
    const [discraption, setDiscraption] = useState('');
    const [images, setImages] = useState([]);
    const [name, setName] = useState('');
    const [avatar, setAvatr] = useState('');
    // const [dalog, setDalog] = useState(false);
    // const [loading, setLoading] = useState(false);
    const [like, setLike] = useState(false);
    const [postId, setPostId] = useState('');

    const history = useHistory();

    const { jwt } = useContext(AuthContext);


    useEffect(() => {
        getData()
     
    }, []);


    const PostLike = async () => {
        const Like = { like: true }

        try {
            const like = await axios.post(`http://localhost:4000/post/65bb6b20c99df721fa772db0/like`, Like, {
                headers: {
                    Authorization: jwt
                }
            })
            const responseData = JSON.parse(like.config.data);
            console.log(responseData.like);
            responseData.like ? setLike(true) : setLike(false);

            
            
        } catch (e) {
            console.log(e)
        }
    }


    const getData = async () => {
        try {
            // setLoading(true)
            const posts = await axios.get(`http://localhost:4000/post/65bb6b20c99df721fa772db0/get-post`)
            setName(posts.data.data.author.name)
            setImages(posts.data.data.img)
            console.log(posts.data.data.img)
            setAvatr(posts.data.data.author.avatar)
            setDiscraption(posts.data.data.discraption)
            setTitle(posts.data.data.title)
            // setLoading(false)

        } catch (e) {
            console.log(e)
            // setLoading(false)
        }
    }

    return (
        <>
            {/* <div className='background'>
                    <div className='center'>
                    <div className='card'>
                        <img src={avatar} alt='null' />
                        <p>{name}</p>
                    </div>
                    <div className='content'>
                      <img className='postImage' src={images} alt='null' />
                      <div className='p-posatin'>
                      <p >{title}</p>
                        <p >{discraption}</p>
                      </div>
                        <i className='like' onClick={() => PostLike()}>
                            {like ? <i style={{ color: 'red' }} className="fa-solid fa-heart"></i> : <i className="fa-regular fa-heart"></i>}
                        </i>
                    </div>
                    </div>

            </div> */}
    <Card sx={{ maxWidth: 345 }}>
      <CardActionArea>
        <CardMedia
          component="img"
          height="140"
          image={images}
          alt="green iguana"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            {title}
          </Typography>
          <Typography variant="body2" color="text.secondary">
           {discraption}
          </Typography>
        </CardContent>
      </CardActionArea>
    </Card>
        </>
    )
}

export default PostImg
