import React, { useEffect, useState } from 'react'
import './Allpost.css'
import axios from 'axios'
function Allpost() {
    const [images, setImages] = useState([]);
    const [loading, setLoading] = useState([]);
    useEffect(() => {
        getPost()
    }, [])
    const getPost = async () => {
        try {
            const response = await axios.get('http://localhost:4000/get-all-post')
            const SaveToimg = response.data.data.map(item => item.img);
            setImages(SaveToimg)
        } catch (e) {
            console.log(e)
        }
    }
    return (
        <>
        {
                 loading ? <CircularProgress  size={70}
                 sx={{
                   position: "fixed",
                   left: "50%",
                   top: "50%",
                   transform: "translate(-50%, -50%)",
                   zIndex: 2}}/> 
                 :  
                 <>
                 
                 
                 </>
        }
        <div className='fullscrean'>
            <div className='post-container'>
                <div className='images'>
                    {
                        images.map((img, index) => (
                            <img key={index} alt='Error' src={img} />
                        )).reverse()
                    }
                </div>
            </div>
        </div>
        </>
    )
}

export default Allpost
