import axios from 'axios';
import React, { useState } from 'react'

function Register() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [avatar, setAvatar] = useState(null);
  const handleAvatarChange = (e) => {
    // تحديث الصورة عند تحديد ملف
    if (e.target.files.length > 0) {
      const selectedAvatar = e.target.files[0];
      setAvatar(URL.createObjectURL(selectedAvatar));
    }
  };
  const onSubmet = async () => {
    const bassData = {
      name,
      email,
      password,
      avatar
    }
    try {
      await axios.post('http://localhost:4000/register', bassData).then(res => console.log(res));
    } catch (e) {
      console.error(e);
    }
  }
  return (
    <>
      <div className='login-contianer'>
        <div >
          <label>الاسم</label>
          <input value={name} onChange={(e)=> setName(e.target.value)} type='text' />
          <br />
          <label>البريد</label>
          <input value={email} onChange={(e)=> setEmail(e.target.value)} type='text' />
          <br />
          <label>كلمه السر</label>
          <input value={password} onChange={(e)=> setPassword(e.target.value)} type='password' />
          <br />
          <label>كلمه السر</label>
          <input value={avatar} onChange={handleAvatarChange} type='file' />
          <img src={avatar}/>
        </div>
        <br />
        <button type='sunmet' onClick={() => {onSubmet()}}>تسجيل الدخول</button>
      </div>
    </>
  )
}

export default Register
