import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios'
import { AuthContext } from '../../context/AuthContext';
import { Storage } from '@capacitor/storage';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';
// import imagess from '../../components/images/Annotation 2024-01-23 201729.png';
import "./postImg.css"

function PostImg() {

    const [title, setTitle] = useState('');
    const [discraption, setDiscraption] = useState('');
    const [images, setImages] = useState([]);
    const [name, setName] = useState('');
    const [avatar, setAvatr] = useState('');
    // const [dalog, setDalog] = useState(false);
    // const [loading, setLoading] = useState(false);
    const [like, setLike] = useState();
    const [postId, setPostId] = useState('');

    const history = useHistory();

    const { jwt } = useContext(AuthContext);


    useEffect(() => {
        getData()
        const storedLike = localStorage.getItem('like');
        if (storedLike !== null) {
            setLike(storedLike === tr);
        }
    }, []);


    const PostLike = async () => {
        const Like = { like: true }

        try {
            const like = await axios.post(`http://localhost:4000/post/65bb6b20c99df721fa772db0/like`, Like, {
                headers: {
                    Authorization: jwt
                }
            })
            const responseData = JSON.parse(like.config.data);
            console.log(responseData.like);
            responseData.like ? setLike(true) : setLike(false);
            localStorage.setItem('like' , responseData.like ? 'true' : 'false')

        } catch (e) {
            console.log(e)
        }
    }


    const getData = async () => {
        try {
            // setLoading(true)
            const posts = await axios.get(`http://localhost:4000/post/65bb6b20c99df721fa772db0/get-post`)
            setName(posts.data.data.author.name)
            setImages(posts.data.data.img)
            setAvatr(posts.data.data.author.avatar)
            setDiscraption(posts.data.data.discraption)
            setTitle(posts.data.data.title)
            // setLoading(false)

        } catch (e) {
            console.log(e)
            // setLoading(false)
        }
    }

    return (
        <>
            <div>
                <div>
                    <div>
                        <img src={avatar} alt='null' />
                        <p>{name}</p>
                        <img src={images} alt='null' />
                        <p>{title}</p>
                        <p>{discraption}</p>
                        <p onClick={() => PostLike()}>
                            {like ? <i style={{ color: 'red' }} className="fa-solid fa-heart"></i> : <i className="fa-regular fa-heart"></i>}
                        </p>

                    </div>
                </div>
            </div>
        </>
    )
}

export default PostImg
