import React, { useState } from 'react';
import './AddNewPost.css';
import axios from 'axios';

function AddNewPost() {

  const [img, setImg] =useState([]);
  const [title, setTitle] = useState('');
  const [discraption, setDiscraption] =useState('');

  
  const onSubmit = async ()=> {

    const data = { 
      title,
      discraption
    }
    const newPost = await axios.post('http://localhost:4000/post', data).then(res=> res)
  }
    
  return (
    <div className='all'>
        <div className='add-container'>
            <div className='form-container'>
                    <label>Add Image</label>
                    <input type='file'/>
                    <label>Title</label>
                    <input value={title} onChange={(e)=> setTitle(e.target.value)} className='text-input' type='text'/>
                    <label>Dicraptions</label>
                    <textarea value={discraption} onChange={(e)=> setDiscraption(e.target.value)} type='text'/>
                    <button className='post-button' type='submit'>نشر</button>
            </div>
        </div>
    </div>
  )
}

export default AddNewPost
