import axios from 'axios';
import React, { useEffect, useState } from 'react'
import "./register.css";
import Alert from '@mui/material/Alert';
import { CircularProgress } from '@mui/material';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';
import { Storage } from '@capacitor/storage';


function Register() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [avatar, setAvatar] = useState('');
  const [seePassword, setSeePassword] = useState('');
  const [loading, setLoading] = useState(false)
  const [Status, setStatus] = useState()


  const history = useHistory();

  const onSubmet = async () => {
    const form = new FormData()
   form.append('name', name)
   form.append('email', email)
   form.append('password', password)
   form.append('avatar', avatar)
    try {
      setLoading(true)
      await axios.post('http://localhost:4000/register', form)
      setLoading(false)
      
    } catch (e) {
      setStatus(e.response.status)
      setLoading(false)
      console.error(e);
    }
    await autoLogin()
  }
  const autoLogin = async ()=> {
    const formLogin = new FormData()
    formLogin.append('name', name)
    formLogin.append('password', password)
    
    try {
        await axios.post('http://192.168.8.112:4000/login', formLogin).then(res => {
        Storage.set({
          key: 'accessToken',
          value: res.data.accessToken
        })
      })
     
    } catch (e) {
      console.log(e)
    }
    history.push('/get-all-post')
  }
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setAvatar(file);
  };
  return (
    <>
    {
      loading ? <CircularProgress  size={70}
      sx={{
        position: "fixed",
        left: "50%",
        top: "50%",
        transform: "translate(-50%, -50%)",
        zIndex: 2}}/> 
      :    
      <>
      <div className='register-contianer'>
        <div className='input-contianer'>
        {avatar ?
                    <img className='imgOfnewPost' alt='Error' src={URL.createObjectURL(avatar)} />
                    :
                    <input className='imgFile' onChange={handleFileChange} type='file' />
                  }
          <label for='name'>اسم المستخدم</label>
          <input required name='name' value={name} onChange={(e) => setName(e.target.value)} type='text' />

          <label for="email">البريد الاكتروني</label>
          <input required name='email' value={email} onChange={(e) => setEmail(e.target.value)} type='text' />

          <label for="password">كلمة المرور</label>
          <input required name='password' value={password} onChange={(e) => setPassword(e.target.value)} type='password' />
          <label for="password2">تأكيد كلمة المرور</label>
          <input required name='password2' onChange={(e) => setSeePassword(e.target.value)} type='password' />
          <button type='sunmet' onClick={()=> onSubmet()}>انشاء الحساب</button>
          <br/>
          {
            password !== seePassword ? <Alert style={{fontSize: '20px'}} className='alert' severity="error">كلمه المرور غير متطابقه</Alert> : ''
          }
          {
            Status == 401 ? <Alert style={{fontSize: '20px', marginTop: '30px'}} className='alert' severity="error">الحساب مسجل اذهب الى لدي حساب بالفعل</Alert> : ''
          }
          <a href='/login'>لدي حساب بالفعل</a>
        </div>
      </div>
    </>
    }
    </>

  )
}

export default Register
