import React, { useContext, useState } from 'react';
import './AddNewPost.css';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext';

function AddNewPost() {

  const {jwt} =useContext(AuthContext);

  const [imgFile, setImgFile] = useState('');
  const [title, setTitle] = useState('');
  const [discraption, setDiscraption] = useState('');
  
  const ReadFile = (e) => {
    const reader = new FileReader();
  
    reader.onload = (event) => {
      // event.target.result يحتوي على البيانات النصية (Data URL) للصورة
      setImgFile(event.target.result);
    };
  
    // قراءة الملف كـ Data URL
    reader.readAsDataURL(e.target.files[0]);
  };
  const onSubmit = async () => {
    const data = { 
      img: imgFile,
      title,
      discraption
    };
    try {
      const newPost = await axios.post('http://localhost:4000/post', JSON.stringify(data), {
        headers: {
          Authorization: jwt
        }
      });
      console.log(data); // افحص البيانات المرتجعة
    } catch (e) {
      console.log(e);
    }
  };
  return (
    <div className='all'>
        <div className='add-container'>
            <div className='form-container'>
                    <label>Add Image</label>
                    {
                      imgFile ? <img style={{width: '200px', height: '200px',maxWidth: '200px', maxHeight: '200px', objectFit: 'cover',}} alt='Eror' src={imgFile}/> : <input  onChange={ ReadFile} type='file'/>
                    }
                    
                    <label>Title</label>
                    <input value={title} onChange={(e)=> setTitle(e.target.value)} className='text-input' type='text'/>
                    <label>Dicraptions</label>
                    <textarea value={discraption} onChange={(e)=> setDiscraption(e.target.value)} type='text'/>
                    <button className='post-button' onClick={onSubmit} type='submit'>نشر</button>
            </div>
        </div>
    </div>
  )
}

export default AddNewPost
