import React, { useContext, useEffect, useState } from 'react';
import './editProfile.css';
import axios from 'axios'
import { AuthContext } from '../../context/AuthContext';
import { Storage } from '@capacitor/storage';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';
// import imagess from '../../components/images/Annotation 2024-01-23 201729.png';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Slide from '@mui/material/Slide';


function EditProfile() {

    const [password , setPassword] = useState('');
    const [images, setImages] = useState([]);
    const [name , setName] = useState('');
    const [avatar , setAvatr] = useState('');
    const [dalog , setDalog] = useState(false);

    const history = useHistory()
    const {jwt} = useContext(AuthContext);
           

    useEffect(()=> {
        getData()
    }, [])
    const updatePro = async ()=> {
        const data=  {
            name,
            password,
            avatar
        }
        try {
            setDalog(true)
            const res = await axios.put('http://localhost:4000/account/update', data, {
                headers: {
                    Authorization: jwt
                }
            }).then(res=> console.error(res))
                Storage.remove({
                    key: "accessToken"
                })
            history.push('/login')
                
        } catch (e) {
            console.log(e)
            setDalog(false)
        }
    }
    const handelClose = ()=> {
        setDalog(false)
    }
    const getData = async ()=>{
        try {
            const posts = await axios.get('http://localhost:4000/get-my-post',
            {
                headers: {
                    Authorization: jwt
                }
            })
            const getPost = posts.data.data.map(item=> item.img)
            setName(posts.data.data[0].author.name)
            setImages(getPost)
            setAvatr(posts.data.data[0].author.avatar)
        } catch (e) {
            console.log(e)
        }
    }


  return (
    <>
      <Dialog
        open={dalog}
        TransitionComponent={Transition}
        keepMounted
        onClose={handelClose}
        aria-describedby="alert-dialog-slide-description"
      >
        <DialogTitle>{"Use Google's location service?"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-slide-description">
          انت على وشك تعديل بياناتك!
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={dalog}>لا</Button>
          <Button onClick={{
            button: {}
          }}>موافق</Button>
        </DialogActions>
      </Dialog>
        <div className='edit-profile'>
            <div className='form'>                
                <img src={avatar} className='edit-img' alt='error'/>
            <div className='edit-form'>
                <input placeholder={name} value={name} onChange={(e)=> setName(e.target.value)}/>
                <input placeholder='Password' type='password' value={password} onChange={(e) => setPassword(e.target.value)} />
            </div>
                <button onClick={updatePro} className='sub-button' type='sumet'>Edit</button>
            </div>
        </div>
        <div className='img-continner'>
            <div className='img-form'>
                {
                    images.map((img, index)=> (
                        <img src={img} key={index}/>
                    )).reverse()
                }
            </div>
        </div>
    </>
  )
}

export default EditProfile
