import "./App.css"
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Headers from './components/header/Headers';
// import Footer from './components/footer/footer';
import Login from './pages/login/Login';
import React, { useContext } from 'react';
import Register from './pages/register/register';
import Allpost from "./pages/allposts/Allpost";
import AuthContextProvider, { AuthContext } from './context/AuthContext'
import EditProfile from "./pages/editProfile/EditProfile";
import AddNewPost from "./pages/AddNewPost/AddNewPost";
import { Redirect } from "react-router-dom/cjs/react-router-dom.min";

function App() {

  const {loggedIn} = useContext(AuthContext);

  return (
    <>
    <AuthContextProvider>
      <Router>
        <Switch>
        <Route path='/register'>
          <Register /> 
        </Route>
        <Route path='/get-all-post'>
          <Headers /> 
          <Allpost/>
        </Route>
        {
          !loggedIn ? <Route path='/login'><Login /> </Route>
          : <Redirect path='get-all-post'/> 

        }
        <Route path='/post'>
          <AddNewPost/>
        </Route>
        <Route path='/account/update'>
          <EditProfile/>
        </Route>
        </Switch>
      </Router>
    </AuthContextProvider>
    </>
  );
}

export default App;
