import React, { useContext, useState } from 'react';
import './AddNewPost.css';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext';

function AddNewPost() {

  const {jwt} =useContext(AuthContext);

  const [img, setImgFile] = useState('');
  const [title, setTitle] = useState('');
  const [discraption, setDiscraption] = useState('');
  
  function convertDataUrlToBuffer(dataUrl) {
    const base64String = dataUrl.split(",")[1];
    return Buffer.from(base64String, "base64");
  }

  const onSubmit = async () => {
    const data = { 
      img,
      title,
      discraption
    };
    try {
      const formdata = new FormData()
      formdata.append('img', data)
      const newPost = await axios.post('http://localhost:4000/post', data, {
        headers: {
          Authorization: jwt
        }
      });
      console.log(newPost.data); // افحص البيانات المرتجعة
    } catch (e) {
      console.log(e);
    }
  };
  return (
    <div className='all'>
        <div className='add-container'>
            <div className='form-container'>
                    <label>Add Image</label>
                    {
                      img ? <img style={{width: '200px', height: '200px',maxWidth: '200px', maxHeight: '200px', objectFit: 'cover',}} alt='Eror' src={img}/> : <input  onChange={(e)=> setImgFile(URL.createObjectURL(e.target.files[0]))} type='file'/>
                    }
                    
                    <label>Title</label>
                    <input value={title} onChange={(e)=> setTitle(e.target.value)} className='text-input' type='text'/>
                    <label>Dicraptions</label>
                    <textarea value={discraption} onChange={(e)=> setDiscraption(e.target.value)} type='text'/>
                    <button className='post-button' onClick={onSubmit} type='submit'>نشر</button>
            </div>
        </div>
    </div>
  )
}

export default AddNewPost
