import React, { useContext, useEffect, useState } from 'react';
import './editProfile.css';
import axios from 'axios'
import { AuthContext } from '../../context/AuthContext';
import imagess from '../../components/images/Annotation 2024-01-23 201729.png'
function EditProfile() {

    const [images , setImages] =useState([]);
    const [name , setName] =useState();
    const {jwt} = useContext(AuthContext);
           
           
    // const res = await axios.put('http://localhost:4000/account/update')
    useEffect(()=> {
        getData()
    }, [])
    const getData = async ()=>{
        try {
            const posts = await axios.get('http://localhost:4000/get-my-post',
            {
                headers: {
                    Authorization: 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2NWI4YjlhNGVkMmI4MjFkZDY0OWY1NjYiLCJuYW1lIjoiYmFuZGFyIiwiZW1haWwiOiJibmRkcmdhbWVyQGdtYWlsLmNvbSIsImlhdCI6MTcwNjYzMDc4NX0.MZ5L33rXfdiYRlBQE9zcRzjXSNNWmMgIOdAPbS-LJdc'
                }
            })
            const getPost = posts.data.data.map(item=> item.img)
            setName(posts.data.data)
            setImages(getPost)
            console.log(posts.data.data.author)
        } catch (e) {
            console.log(e)
        }
    }

  return (
    <>
        <div className='edit-profile'>
            <div className='form'>                
                <img src={imagess} className='edit-img' alt='error'/>
            <div className='edit-form'>
                
                <input placeholder='Name'/>
                
                <input placeholder='pssword'/>
            </div>
                <button onClick={getData} className='sub-button' type='sumet'>Edit</button>
            </div>
        </div>
        <div className='img-continner'>
            <div className='img-form'>
                {
                    images.map((img, index)=> (
                        <img src={img} key={index}/>
                    ))
                }
            </div>
        </div>
    </>
  )
}

export default EditProfile
