import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios'
import { AuthContext } from '../../context/AuthContext';
import { Storage } from '@capacitor/storage';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';
// import imagess from '../../components/images/Annotation 2024-01-23 201729.png';
import { styled } from '@mui/material/styles';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Collapse from '@mui/material/Collapse';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import FavoriteIcon from '@mui/icons-material/Favorite';
import './postImg.css'
import { NativeSelect } from '@mui/material';

function PostImg() {

    const [title, setTitle] = useState('');
    const [discraption, setDiscraption] = useState('');
    const [images, setImages] = useState([]);
    const [name, setName] = useState('');
    const [avatar, setAvatr] = useState('');
    // const [dalog, setDalog] = useState(false);
    // const [loading, setLoading] = useState(false);
    const [like, setLike] = useState(false);
    const [postId, setPostId] = useState('');

    const history = useHistory();

    const { jwt } = useContext(AuthContext);


    useEffect(() => {
        getData()

    }, []);


    const PostLike = async () => {
        const Like = { like: true }

        try {
            const like = await axios.post(`http://localhost:4000/post/65bb6b20c99df721fa772db0/like`, Like, {
                headers: {
                    Authorization: jwt
                }
            })
            const responseData = JSON.parse(like.config.data);
            console.log(responseData.like);
            responseData.like ? setLike(true) : setLike(false);



        } catch (e) {
            console.log(e)
        }
    }


    const getData = async () => {
        try {
            // setLoading(true)
            const posts = await axios.get(`http://localhost:4000/post/65bb6b20c99df721fa772db0/get-post`)
            setName(posts.data.data.author.name)
            setImages(posts.data.data.img)
            console.log(posts.data.data.img)
            setAvatr(posts.data.data.author.avatar)
            setDiscraption(posts.data.data.discraption)
            setTitle(posts.data.data.title)
            // setLoading(false)

        } catch (e) {
            console.log(e)
            // setLoading(false)
        }
    }

    return (
        <>
            <div className='background'>
                <div className='nav-card'>

                    <CardHeader
                        avatar={
                            <Avatar src={avatar}  aria-label="recipe"></Avatar>
                        }
                        
                        title={name}
                    />
                </div>
                <div className='card'>
                    <CardMedia
                    style={{minHeight: 'auto'}}
                        component="img"
                        height="194"
                        image={images}
                        alt="Paella dish"
                    />
                <Card sx={{ width: 'auto', }}>
                    <CardContent>
                        <Typography variant="body2" color="text.secondary">
                        {title}
                        
                        </Typography>
                    </CardContent>
                    <CardContent>
                        <Typography variant="body2" color="text.secondary">
                        {discraption}
                        </Typography>
                    </CardContent>
                    <CardActions disableSpacing>
                        <IconButton aria-label="add to favorites">
                        <FavoriteIcon/>
                        </IconButton>
                    </CardActions>
                </Card>
                </div>
            </div>
        </>
    )
}

export default PostImg
